element.calculation:
(function calculatedFieldValue(current) {
        var rate;

    var timesgr = new GlideRecord('x_998267_project_m_time_entries'); 
	timegr.addQuery('project', current);
    timegr.query();
    while (timegr.next()) {
         var ratetime = timegr.time_spent.getValue();
        rate += ratetime*timegr.rate_value.getValue();
    }
    return current.allocated_funds-rate; // return the calculated value

})(current);

(function calculatedFieldValue(current) {
     
	// Add your code here
	return current.allocated_funds-current.cost;  // return the calculated value

})(current);
