sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
	var currency_amount;
	var currency_code = 'USD';
    if (newValue == 'Junior Dev') {
         currency_amount = parseInt(200);

        g_form.setValue('rate_value',currency_code + ';' + currency_amount);
    } else
    if (newValue == 'HR Manager') {
        currency_amount = parseInt(400);

        g_form.setValue('rate_value', currency_code + ';' + currency_amount);
    } else {
         currency_amount = parseInt(500);

        g_form.setValue('rate_value', currency_code + ';' + currency_amount);
    }


}
